import './App.css';
import { useState, useEffect,useRef } from 'react';

function Stopwatch() {
const [timer, setTimer ] = useState(0)
const [isActive, setIsActive] = useState(false)
const [isPaused, setIsPaused] = useState(false)
const increment = useRef(null)

// useEffect( () => {
//   startTimer();
// },[]);

  const startTimer = () => {
    setIsActive(true)
    setIsPaused(true)
    increment.current = setInterval(() => {
      setTimer((timer) => timer + 1)
    }, 10)
  }

  const stopTimer = () => {
    clearInterval(increment.current)
    setIsPaused(false)
  };

  const handleResume = () => {
    setIsPaused(true)
    increment.current = setInterval(() => {
      setTimer((timer) => timer + 1)
    }, 10)
  }

  const resetTimer = () => {
    clearInterval(increment.current)
    setIsActive(false)
    setIsPaused(false)
    setTimer(0)
  };


    const seconds = `0${(timer % 60)}`.slice(-2)
    const minute = `${Math.floor(timer / 60)}`
    const minutes = `0${minute % 60}`.slice(-2)
    const hours = `0${Math.floor(timer / 3600)}`.slice(-2)

  return (
    <div className="Stopwatch">
      <header className="Stopwatch-header">
      <div className="Stopwatch-display">
      {hours} : {minutes} : {seconds} 
      </div>
      <div>
        { !isActive && !isPaused ?
              <button onClick={startTimer}>Start</button>
              : (
                isPaused ? <button onClick={stopTimer}>Pause</button> :
                  <button onClick={handleResume}>Resume</button>
              )
          }
          <button onClick={resetTimer} disabled={!isActive}>Reset</button>
      </div>
      </header>
    </div>
  );
}

export default Stopwatch;
