import './App.css';
import { useState, useEffect,useRef } from 'react';

function Stopwatch() {
const [timer, setTimer ] = useState(0)
const [isActive, setIsActive] = useState(false)
const [isPaused, setIsPaused] = useState(false)
const increment = useRef(null)

// useEffect( () => {
//   startTimer();
// },[]);

  const startTimer = () => {
    setIsActive(true)
    setIsPaused(true)
    increment.current = setInterval(() => {
      setTimer((timer) => timer + 1)
    }, 1000)
  }

  const stopTimer = () => {
    clearInterval(increment.current)
    setIsPaused(false)
  };

  const handleResume = () => {
    setIsPaused(true)
    increment.current = setInterval(() => {
      setTimer((timer) => timer + 1)
    }, 1000)
  }

  const resetTimer = () => {
    clearInterval(increment.current)
    setIsActive(false)
    setIsPaused(false)
    setTimer(0)
  };

  // stopTimer = () => {
  //   this.setState({ timerOn: false });
  //   clearInterval(this.timer);
  // };

  // resetTimer = () => {
  //   this.setState({
  //     timerStart: 0,
  //     timerCounter: 0
  //   });
  // };



  const formatTime = () => {
    let centiseconds = ("0" + (Math.floor(timerCounter / 10) % 100)).slice(-2);
    let seconds = ("0" + (Math.floor(timerCounter / 1000) % 60)).slice(-2);
    let minutes = ("0" + (Math.floor(timerCounter / 60000) % 60)).slice(-2);
    let hours = ("0" + Math.floor(timerCounter / 3600000)).slice(-2);
    return `${hours} : ${minutes} : ${ seconds}`
  }
  return (
    <div className="Stopwatch">
      <header className="Stopwatch-header">
      <div className="Stopwatch-display">
          {formatTime}
      </div>
      <div>
      {timeOn === false &&(
          <button onClick={startTimer}>Start</button>
        )}
        {timeOn === true && (
          <button onClick={stopTimer}>Stop</button>
        )}
        {timeOn === false && timerCounter > 0 && (
          <button onClick={handleResume}>Resume</button>
        )}
        {timeOn === false && timerCounter > 0 && (
          <button onClick={resetTimer}>Reset</button>
        )}
      </div>
      </header>
    </div>
  );
}

export default Stopwatch;







