import './App.css';
import { useState, useEffect,useRef } from 'react';

function Stopwatch() {
const [timeOn, setTimeOn ] = useState(false)
const [startTime, setStartTime] = useState(0);
const [timerCounter, setTimerCounter] = useState(0);

const timer = useRef(null)

// useEffect( () => {
//   startTimer();
// },[]);

  const startTimer = () => {
    setTimeOn(true);
    setTimerCounter(timerCounter);
    setStartTime(Date.now() - timerCounter)
    timer = setInterval( () => {
      setTimerCounter(Date.now() - startTime);
    },10);
  }

  const stopTimer = () => {
    setTimeOn(false);
    clearInterval(timer);
  };

  const resetTimer = () => {
      setStartTime(0);
      setTimerCounter(0);
  };

  // stopTimer = () => {
  //   this.setState({ timerOn: false });
  //   clearInterval(this.timer);
  // };

  // resetTimer = () => {
  //   this.setState({
  //     timerStart: 0,
  //     timerCounter: 0
  //   });
  // };




    let centiseconds = ("0" + (Math.floor(timerCounter / 10) % 100)).slice(-2);
    let seconds = ("0" + (Math.floor(timerCounter / 1000) % 60)).slice(-2);
    let minutes = ("0" + (Math.floor(timerCounter / 60000) % 60)).slice(-2);
    let hours = ("0" + Math.floor(timerCounter / 3600000)).slice(-2);
  return (
    <div className="Stopwatch">
      <header className="Stopwatch-header">
      <div className="Stopwatch-display">
          {hours} : {minutes} : {seconds} : {centiseconds}
      </div>
      <div>
      {timeOn === false &&(
          <button onClick={startTimer}>Start</button>
        )}
        {timeOn === true && (
          <button onClick={stopTimer}>Stop</button>
        )}
        {timeOn === false && timerCounter > 0 && (
          <button onClick={startTimer}>Resume</button>
        )}
        {timeOn === false && timerCounter > 0 && (
          <button onClick={resetTimer}>Reset</button>
        )}
      </div>
      </header>
    </div>
  );
}

export default Stopwatch;
