import './App.css';
import { useState, useEffect,useRef } from 'react';
import Flexbox from 'flexbox-react';
import Time from './components/Time/Time'

function Stopwatch() {
const [timer, setTimer ] = useState(0)
const [timerOn, setTimerOn] = useState(false)
const [pauseOn, setPauseOn] = useState(false)
const increment = useRef(null)

useEffect( () => {

},[]);

  const startTimer = () => {
    setTimerOn(true)
    setPauseOn(true)
    increment.current = setInterval(() => {
      setTimer((timer) => timer + 1)
    }, 1000)
  }

  const stopTimer = () => {
    clearInterval(increment.current)
    setPauseOn(false)
  };

  const resetTimer = () => {
    clearInterval(increment.current)
    setTimerOn(false)
    setPauseOn(false)
    setTimer(0)
    startTimer()
  };




  return (
    <div className="Stopwatch">
      <header className="Stopwatch-header">
        <Time timer={timer}/>
      <div>
        <Flexbox flexDirection="row" justifyContent="center">
        {!timerOn && !pauseOn ? <button onClick={startTimer}>Start</button>
              : (
                pauseOn ? <button onClick={stopTimer}>Pause</button> :
                  <button onClick={startTimer}>Start</button>
              )
          }
          <button onClick={resetTimer} disabled={!timerOn}>Reset</button>
          </Flexbox>
      </div>
      </header>
    </div>
  );
}

export default Stopwatch;


