import './App.css';
import { useState, useEffect} from 'react';
import Flexbox from 'flexbox-react';
import Time from './components/Time/Time'
import { Observable } from "rxjs";




function Stopwatch() {
const [timer, setTimer ] = useState(0);
const [stopIncrement, setStopIncrement] = useState(0);
const [timerOn, setTimerOn] = useState(false);;
let increment = null;


let timerStream$ = Observable.create(function subscribe(observer){
  const interval = setInterval(() => observer.next(timer +1),1000)

  return function unsubscribe() {
    clearInterval(interval);
  }
});

var unsubscribe = subscribe(timer => setTimer(timer))
const startTimer = () => {
  setTimerOn(true)
  timerStream$.subscribe(timer => setTimer(timer))
  };

const stopTimer  = () => {
  unsubscribe();
}

  const resetTimer = () => {
    clearInterval(timerStream$.subscribe(0));
    setTimerOn(false);
    setTimer(0);
    startTimer();
  };

  const waitTimer = () => {
    clearInterval(timerStream$.subscribe());
    setTimerOn(false);
  };

  return (
    <div className="Stopwatch">
      <header className="Stopwatch-header">
        <Time timer={timer}/>
      <div>
        <Flexbox flexDirection="row" justifyContent="center">
        {!timerOn ? <button onClick={startTimer}>Start</button>
             : <button onClick={stopTimer}>Stop</button>
           }
            <button onDoubleClick={waitTimer}>Wait</button>
           <button onClick={resetTimer} >Reset</button> 
        </Flexbox> 
      </div>
      </header>
    </div>
  );
}

export default Stopwatch;

