import './App.css';
import { useState, useEffect} from 'react';
import Flexbox from 'flexbox-react';
import Time from './components/Time/Time'
import { interval, Observable } from "rxjs";
import { map } from "rxjs/operators";

const delay = 1000;

function Stopwatch() {
const [timer, setTimer ] = useState(0);
const [diff, setDiff] = useState(0);
const [timerOn, setTimerOn] = useState(false);;
const [subscription, setSubscription] = useState("");
const [prevent, setPrevent] = useState(true);

let timerStream$ = new Observable(observer => {
    const interval = setInterval(() => observer.next(timer =>timer +1),1000)
    return () => clearInterval(interval)
});


const startTimer = () => {
  if (!subscription) {
    const timerSubscription = interval(delay)
      .pipe(map((v) => v + 1))
      .subscribe((v) => {
        setTimer(v + diff);
      });
    setSubscription(timerSubscription);
  } else {
    subscription.unsubscribe();
    setTimer(0);
    setDiff(0);
    setSubscription("");
  }
  };

const stopTimer  = () => {
  subscription.unsubscribe();
}

  const resetTimer = () => {
    clearInterval(timerStream$.subscribe(0));
    setTimerOn(false);
    setTimer(0);
    startTimer();
  };

  const waitTimer = () => {
    if (prevent) {
      setPrevent(false);
      const timerInstance = setTimeout(function () {
        setPrevent(true);
        clearTimeout(timerInstance);
      }, 300);
    } else {
      if (subscription) {
        subscription.unsubscribe();
      }

      setDiff(timer);
      setSubscription("");
    }
  };

  return (
    <div className="Stopwatch">
      <header className="Stopwatch-header">
        <Time timer={timer}/>
      <div>
        <Flexbox flexDirection="row" justifyContent="center">
        {!timerOn ? <button onClick={startTimer}>Start</button>
             : <button onClick={stopTimer}>Stop</button>
           }
            <button onDoubleClick={waitTimer}>Wait</button>
           <button onClick={resetTimer} >Reset</button> 
        </Flexbox> 
      </div>
      </header>
    </div>
  );
}

export default Stopwatch;

